package looping;

public class exercicio04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 1;
		while (x <= 100) {
			if (x % 2 == 1) {
				System.out.print(x + " ");
			}x++;			
		}
		x = 1;
		System.out.println(" ");
		while (x <= 100) {
			if (x % 2 == 0) {
			System.out.print(x + " ");
		}x++;
	}
	}
}
